INSERT INTO api_riskquestion VALUES(1, 'Do you have *more than 100 moles*?');
INSERT INTO api_riskquestion VALUES(2, 'Do you have *over 3 suspicious moles* (look *different than your other moles*)?');
INSERT INTO api_riskquestion VALUES(3, 'Do you have a *pale skin* which *burns easily* (potentially combined with *red* or *light hair*, *light eyes* and *freckles*)?');
INSERT INTO api_riskquestion VALUES(4, 'Did you experience *severe sunburns (with blisters)* during your *childhood*');
INSERT INTO api_riskquestion VALUES(5, 'On average, do you go outside for *more than 2 hours* a day from 10AM to 12PM *without protecting your skin* from sunlight (sunscreen or covering clothing)';
INSERT INTO api_riskquestion VALUES(6, 'Have you ever been *treated* for *one or more malignant moles (melanoma)* or *another form of skin cancer*?');
INSERT INTO api_riskquestion VALUES(7, 'Do you have at least *3 family members* who experienced malignant or suspicious moles (*only parents or children*)?');